var s = require("includes/request_server");
module.exports = {
  g:function(v, d){ //value , default value
	if(v==null){
		return d;
	}else{
		return v;
	}
 },
  remove:function(number, items){
	get_id=-1;
	items.forEach(function(obj){
		if(obj.number==number){
			get_id=obj.id;
		}
	});
	if(get_id!=-1){
		s.requestS(null,"delete","favorites","/"+get_id,null);
	}else{
		console.log("Favorite not found");
	}
  }
};
