var server = require("includes/request_server");
module.exports = {
	get: function(value, def){ //value , default value
		if(value==null){
			return def;
		}else{
			return value;
		}
	},
	remove: function(number, items){
		get_id = -1;
		items.forEach(function(obj){
			if(obj.number==number){
				get_id=obj.id;
			}
		});
		if(get_id!=-1){
			server.requestS(null, "delete", "favorites", "/"+get_id, null);
		}else{
			console.log("Favorite not found");
		}
	}
};
