
module.exports = {
	requestS: function(url, method, action, options, data, silent, number){
		var request = require("ajax-request");
		var helper = require("includes/helper_functions");
		url = helper.get(url, "http://127.0.0.1:3000");
		method = helper.get(method, "GET");
		action = helper.get(action, "favorites");
		options = helper.get(options, "");
		data=helper.get(data, "");
		silent=helper.get(silent, false);
		api = "/api/v1/";
		final_url=url + api + action + options;
		request({
			url: final_url,
			method: method,
			data: data
			},
			function(err, res, body) {
				var json = JSON.parse(body);
				if(!silent){
					json.forEach(function(obj){
						console.log(obj);
					});
				}else{
					helper.remove(number, json);
				}
			}			  
		);
	}
};
