
module.exports = {
	  requestS:function(url,method, action, options,data,silent, number){
	  	  var request = require("ajax-request");
		  var h = require("includes/helper_functions");
		  url = h.g(url,"http://127.0.0.1:3000");
		  method = h.g(method,"GET");
		  action = h.g(action,"favorites");
		  options = h.g(options,"");
		  data=h.g(data,"");
		  silent=h.g(silent,false);
		  api = "/api/v1/";
		  final_url=url+api+action+options;
		  
		  console.log(final_url);
		  request({
		  	url:final_url,
		  	method:method,
		  	data:data
		  	},
		  	function(err, res, body) {
			  var json = JSON.parse(body);
			  if(!silent){
				  json.forEach(function(obj){
				  	console.log(obj);
				  });
			  }else{
			  	h.remove(number,json);
			  }
			}
			  
		  );
	}
};
